import React, {useState, useEffect} from 'react'
import ReactDOM from 'react-dom'
import './index.css'

const Button = ({handleClick, text}) => {
  return (
    <button onClick = {handleClick}>
      {text}
    </button>
  )
}

const Song = ({link}) => {
  return (
    <>
      <iframe title = "pog" src={link} width="300" height="80" frameborder="0" allowtransparency="true" allow="encrypted-media"></iframe>
      <br/>
    </>
  )
}

const App = () => {
  const [songs, setSongs] = useState([])
  const [isLoaded, setIsLoaded] = useState(false);
  const [newSong, setNewSong] = useState('')
  useEffect(() => {
    fetch("http://localhost:3001/songs")
      .then(res => res.json())
      .then((result) => {
        setSongs(result)
        setIsLoaded(true)
      })
  }, [])

  const pog1 = () => {console.log("pog1")}
  const pog2 = () => {console.log("pog2")}

  const addSong = (event) => {
    event.preventDefault()
    const songObject = {
      id: songs.length + 1,
      link: newSong,
      ranking: 1
    }
    songs.concat(songObject)
    setNewSong('')
  }

  const handleSongChange = (event) => {
    setNewSong(event.target.value)
  }

  if (!isLoaded) {
    return <div>Loading...</div>
  } else {
    return (
      <div>
        <h1>
          pog
        </h1>
        <div class="container">
          <div class="fixed">
            <h2>
              Song 1
            </h2>
            <Song link={songs[0].link}/>
            <Button handleClick={pog1} text='pog1'/>
          </div>
          <div class="grow">
            <h2>
              Song 2
            </h2>
            <Song link={songs[1].link}/>
            <Button handleClick={pog2} text='pog2'/>
          </div>
        </div>
        <form onSubmit={addSong}>
          <input 
            value={newSong}
            onChange={handleSongChange}
          />
          <button type="submit">Add</button>
        </form>
      </div>
    )
  }
}

ReactDOM.render(<App />, document.getElementById('root'))
