import React, {useState, useEffect} from 'react'
import ReactDOM from 'react-dom'
import './index.css'

const Button = ({handleClick, text}) => {
  return (
    <button onClick = {handleClick}>
      {text}
    </button>
  )
}

const Song = ({link}) => {
  return (
    <>
      <iframe title = "pog" src={link} width="300" height="80" frameborder="0" allowtransparency="true" allow="encrypted-media"></iframe>
      <br/>
    </>
  )
}

const App = () => {
  const [songs, setSongs] = useState([])
  const [isLoaded, setIsLoaded] = useState(false);
  const [newSong, setNewSong] = useState('')
  const [song1, setSong1] = useState(0)
  const [song2, setSong2] = useState(0)

  const getRandomInt = (max) => {return Math.floor(Math.random() * Math.floor(max))}

  useEffect(() => {
    fetch("http://localhost:3001/songs")
    .then(res => res.json())
    .then((result) => {
      setSongs(result)
      setIsLoaded(true)
      const n1 = getRandomInt(result.length)
      setSong1(n1)
      let n2 = getRandomInt(result.length)
      while (n2 === n1 & result.length > 2) {
        n2 = getRandomInt(result.length)
      }
      setSong2(n2)
      console.log(n1, n2)
    })
  }, [])

  const probability = (a, b) => {
    return 1/(1+Math.pow(10, (a-b)/400))
  }

  const updateElo = (a, b) => {
    const s1 = songs[a]
    const s2 = songs[b]
    const a_old = s1.rating
    const b_old = s2.rating
    const a_new = a_old + 20 * (1-probability(b_old, a_old))
    const b_new = b_old + 20 * (0-probability(a_old, b_old))
    const url1 = `http://localhost:3001/songs/${s1.id}`
    const url2 = `http://localhost:3001/songs/${s2.id}`
    const updated_s1 = {...s1, rating: a_new}
    const updated_s2 = {...s2, rating: b_new}

    Promise.all([
      fetch(url1, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updated_s1)
      }),
      fetch(url2, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updated_s2)
      })
    ]).then(res => Promise.all(res.map(n => n.json())))
    .then(data => {
      let new_songs = songs.slice()
      new_songs[a] = data[0]
      new_songs[b] = data[1]
      setSongs(new_songs)
    })

    // fetch(url1, {
    //   method: 'PUT',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(updated_s1)
    // }).then(res => res.json())
    //   .then(result => {
    //     let new_songs = songs.slice()
    //     new_songs[a] = result
    //     setSongs(new_songs)
    //   }).then(console.log(songs))



    // fetch(url2, {
    //   method: 'PUT',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(updated_s2)
    // }).then(res => res.json())
    //   .then(result =>  {
    //     let new_songs = songs.slice()
    //     new_songs[b] = result
    //     setSongs(new_songs)
    //   })
  }

  const updateSongs = () => {
    const n1 = getRandomInt(songs.length)
    const n2 = getRandomInt(songs.length)
    console.log(n1, n2)
    setSong1(n1)
    setSong2(n2)
  }

  const pog1 = () => {
    updateElo(song1, song2)
    updateSongs()
  }

  const pog2 = () => {
    updateElo(song2, song1)
    updateSongs()
  }

  const addSong = (event) => {
    event.preventDefault()
    const n = newSong.indexOf('/track/')
    const songLink = newSong.substr(0, n) + '/embed' + newSong.substr(n)
    const songObject = {
      link: songLink
    }
    fetch("http://localhost:3001/songs", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(songObject)
    }).then(response => {
      console.log(response)
      setSongs(songs.concat(response.data))
      setNewSong('')
    })
  }

  const handleSongChange = (event) => {
    setNewSong(event.target.value)
  }


  if (!isLoaded) {
    return <div>Loading...</div>
  } else {
    return (
      <div>
        <h1>
          pog
        </h1>
        <div class="container">
          <div class="fixed">
            <h2>
              Song 1
            </h2>
            <Song link={songs[song1].link}/>
            <Button handleClick={pog1} text='pog1'/>
          </div>
          <div class="grow">
            <h2>
              Song 2
            </h2>
            <Song link={songs[song2].link}/>
            <Button handleClick={pog2} text='pog2'/>
          </div>
        </div>
        <form onSubmit={addSong}>
          <input 
            value={newSong}
            onChange={handleSongChange}
          />
          <button type="submit">Add</button>
        </form>
      </div>
    )
  }
}

ReactDOM.render(<App />, document.getElementById('root'))
